import React from 'react';

function Plan(props) {
  const [editable, setEditable] = React.useState(false);
  const [updatedText, setUpdatedText] = React.useState(props.value);

  const handleUpdate = () => {
    props.updateData(props.id, updatedText);
    setEditable(false);
  };

  return (
    <>
      <li className="shadow p-2 my-2 col-sm-9">
        {editable ? (
          <input
            type="text"
            className="form-control"
            value={updatedText}
            onChange={(e) => setUpdatedText(e.target.value)}
          />
        ) : (
          props.value
        )}
      </li>
      <button
        className="btn btn-danger my-2 col-sm-2 offset-1"
        onClick={() => {
          props.sendData(props.id);
        }}
      >
        X
      </button>
      {editable ? (
        <button className="btn btn-primary" onClick={handleUpdate}>
          Save
        </button>
      ) : (
        <button className="btn btn-secondary" onClick={() => setEditable(true)}>
          Update
        </button>
      )}
    </>
  );
}

export default Plan;
